// utils/api.js
export const fetchNews = async (search, page, limit) => {
    try {
        const response = await fetch(`https://newsapi.org/v2/everything?q=${search}&page=${page}&pageSize=${limit}&apiKey=YOUR_API_KEY`);
        if (!response.ok) {
            throw new Error('Error de red. Intenta más tarde.');
        }
        const data = await response.json();
        return data.articles;
    } catch (error) {
        console.error('Error al obtener noticias:', error);
        throw error;
    }
};
