import React, { useEffect, useState } from 'react';
import { fetchNews } from '../utils/api'; 
import { translations, MAX_REQUESTS } from '../utils/constants'; 
import '../styles/index.css'; 
import '../styles/App.css'; 
import LanguageToggle from './LanguageToggle'; 
import ErrorMessage from './ErrorMessage';
import Loading from './Loading';
import SearchBar from './SearchBar'; 

const NewsApp = () => {
    const [search, setSearch] = useState("");
    const [newsData, setNewsData] = useState([]);
    const [page, setPage] = useState(1);
    const [language, setLanguage] = useState("es");
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [requestCount, setRequestCount] = useState(0);

    const getData = async () => {
        if (requestCount >= MAX_REQUESTS) {
            setError(translations[language].requestLimitReached);
            return;
        }

        setLoading(true);
        setError(null);

        try {
            const data = await fetchNews(search, page);

            console.log("Datos recibidos:", data); // Verifica los datos

            if (!data || !Array.isArray(data)) {
                throw new Error(translations[language].invalidResponse);
            }

            setNewsData((prevData) => [...prevData, ...data]);
            setRequestCount(prevCount => prevCount + 1);
        } catch (error) {
            setError(error.message || translations[language].genericError);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        if (search) {
            setNewsData([]);
            setPage(1);
            setRequestCount(0);
        }
    }, [search]);

    useEffect(() => {
        if (page > 1 || search) {
            getData();
        }
    }, [page, search]);

    const handleInput = (e) => {
        setSearch(e.target.value);
    };

    const handleSearch = () => {
        setPage(1);
        setNewsData([]);
        getData();
    };

    const handleKeyDown = (e) => {
        if (e.key === 'Enter') {
            handleSearch(); 
        }
    };

    const toggleLanguage = () => {
        setLanguage((prevLanguage) => (prevLanguage === "es" ? "en" : "es"));
        setNewsData([]);
        setPage(1);
        setError(null);
    };

    return (
        <div className="App">
            <header className="header">
                <h1>{translations[language].title}</h1>
            </header>
            <div className="mainContent">
                <main className="centralContent">
                    <p className='head'>{translations[language].header}</p>
                    <div>
                        {loading ? (
                            <Loading /> 
                        ) : error ? (
                            <ErrorMessage message={error} /> 
                        ) : newsData.length > 0 ? (
                            newsData.map((article, index) => (
                                <div key={index} className="news-item">
                                    <h3>{article.title}</h3>
                                    <p>{article.description}</p>
                                    <a href={article.url} target="_blank" rel="noopener noreferrer">Leer más</a>
                                </div>
                            ))
                        ) : (
                            <p>{translations[language].noNews}</p>
                        )}
                    </div>
                </main>
                <aside className="sidebar rightSidebar">
                    <SearchBar 
                        value={search}
                        onChange={handleInput}
                        onKeyDown={handleKeyDown}
                        onSearch={handleSearch}
                        placeholder={translations[language].placeholder}
                        searchButtonText={translations[language].searchButton}
                    />
                    <div className="buttonContainer">
                        <LanguageToggle toggleLanguage={toggleLanguage} currentLanguage={language} />
                    </div>
                </aside>
            </div>
            <footer className="footer">
                <p>Isaac Barco</p>
            </footer>
        </div>
    );
};

export default NewsApp;
