import React, { useEffect, useState } from 'react';
import Card from './Card';
import '../index.css'; // Asegúrate de que la ruta sea correcta

const translations = {
    es: {
        title: "Prueba Técnica - Grupo Transoceánica",
        placeholder: "Buscar noticias",
        searchButton: "Buscar",
        header: "Mantente Actualizado con News API",
        categories: {
            sports: "Deportes",
            politics: "Política",
            entertainment: "Entretenimiento",
            health: "Salud",
            technology: "Tecnología",
        },
        changeLanguage: "Cambiar a Inglés",
        requestLimitReached: "Límite de solicitudes alcanzado. Intenta más tarde.",
    },
    en: {
        title: "Test - Transoceanica Group",
        placeholder: "Search News",
        searchButton: "Search",
        header: "Stay Updated with News API",
        categories: {
            sports: "Sports",
            politics: "Politics",
            entertainment: "Entertainment",
            health: "Health",
            technology: "Technology",
        },
        changeLanguage: "Switch to Spanish",
        requestLimitReached: "Request limit reached. Please try again later.",
    },
};

const Newsapp = () => {
    const [search, setSearch] = useState("");
    const [newsData, setNewsData] = useState([]);
    const [page, setPage] = useState(1);
    const [language, setLanguage] = useState("es");
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [requestCount, setRequestCount] = useState(0); // Contador de solicitudes
    const MAX_REQUESTS = 100; // Ajusta según el límite de tu plan
    const API_KEY = "deafbf7d5bc7485cb8d7761f2e0b379d";

    const getData = async () => {
        if (requestCount >= MAX_REQUESTS) {
            setError(translations[language].requestLimitReached);
            return;
        }

        // Verificación de búsqueda vacía
        if (!search.trim()) {
            setError("Por favor, ingresa un término de búsqueda.");
            return;
        }

        setLoading(true);
        setError(null);
        const cacheKey = `news_${search}_${language}_${page}`;
        const cachedData = localStorage.getItem(cacheKey);

        if (cachedData) {
            setNewsData(JSON.parse(cachedData));
            setLoading(false);
            return; // Usa datos del caché
        }

        try {
            const response = await fetch(`https://newsapi.org/v2/everything?q=${search}&language=${language}&apiKey=${API_KEY}&pageSize=10&page=${page}`);
            if (!response.ok) {
                const errorDetails = await response.json();
                throw new Error(`Error ${response.status}: ${errorDetails.message}`);
            }

            const jsonData = await response.json();
            if (jsonData.status === 'ok' && jsonData.articles.length > 0) {
                setNewsData((prevData) => [...prevData, ...jsonData.articles]);
                localStorage.setItem(cacheKey, JSON.stringify(jsonData.articles)); // Guarda en caché
                setRequestCount((prevCount) => prevCount + 1); // Incrementa el contador
            } else {
                console.log("No se encontraron artículos.");
                setNewsData([]);
            }
        } catch (error) {
            console.error("Error fetching news: ", error);
            setError("Error al cargar las noticias. " + error.message);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        getData();
    }, [page, search, language]);

    useEffect(() => {
        const handleScroll = () => {
            if (window.innerHeight + document.documentElement.scrollTop + 1 >= document.documentElement.offsetHeight) {
                console.log("Loading more news...");
                setPage((prevPage) => prevPage + 1);
            }
        };
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    const handleInput = (e) => {
        setSearch(e.target.value);
    };

    const userInput = (event) => {
        const category = event.target.value;
        setSearch(category);
        setPage(1);
        setNewsData([]);
        getData();
    };

    const handleSearch = () => {
        setPage(1);
        setNewsData([]);
        getData();
    };

    const toggleLanguage = () => {
        setLanguage((prevLanguage) => (prevLanguage === "es" ? "en" : "es"));
        setNewsData([]);
        getData();
    };

    return (
        <div className="App">
            <header className="header">
                <h1>{translations[language].title}</h1>
            </header>
            <div className="mainContent">
                <aside className="sidebar leftSidebar">
                    <div className='categoryBtn'>
                        <div className="textContainer">
                            <p className='interes'>Temas de Interés</p>
                        </div>
                        <button onClick={userInput} value="deportes">{translations[language].categories.sports}</button>
                        <button onClick={userInput} value="politica">{translations[language].categories.politics}</button>
                        <button onClick={userInput} value="entretenimiento">{translations[language].categories.entertainment}</button>
                        <button onClick={userInput} value="salud">{translations[language].categories.health}</button>
                        <button onClick={userInput} value="tecnologia">{translations[language].categories.technology}</button>
                    </div>
                </aside>
                <main className="centralContent">
                    <p className='head'>{translations[language].header}</p>
                    <div>
                        {loading ? (
                            <p>Cargando noticias...</p>
                        ) : error ? (
                            <p>{error}</p> // Muestra el error si existe
                        ) : newsData.length > 0 ? (
                            <Card data={newsData} />
                        ) : (
                            <p>No hay noticias disponibles.</p>
                        )}
                    </div>
                </main>
                <aside className="sidebar rightSidebar">
                    <div className='searchBar'>
                        <input
                            type='text'
                            placeholder={translations[language].placeholder}
                            value={search}
                            onChange={handleInput}
                        />
                        <button onClick={handleSearch}>{translations[language].searchButton}</button>
                    </div>
                    <div className="buttonContainer">
                        <button className="languageButton" onClick={toggleLanguage}>
                            {translations[language].changeLanguage}
                        </button>
                    </div>
                </aside>
            </div>
            <footer className="footer">
                <p>Isaac Barco</p>
            </footer>
        </div>
    );
};

export default Newsapp;
