import React, { useEffect, useState } from 'react';
import Card from './Card';
import { fetchNews } from '../utils/api'; // Ajusta la ruta de api
import { translations, MAX_REQUESTS } from '../utils/constants'; // Ajusta la ruta de constants
import '../styles/index.css'; // Asegúrate de que esta ruta sea correcta
import '../styles/App.css'; // Asegúrate de que esta ruta sea correcta
import LanguageToggle from './LanguageToggle'; // Importa el componente LanguageToggle
import ErrorDisplay from './ErrorDisplay'; // Importa el componente ErrorDisplay
import Loader from './Loader'; // Importa el componente Loader

const NewsApp = () => {
    const [search, setSearch] = useState("");
    const [newsData, setNewsData] = useState([]);
    const [page, setPage] = useState(1);
    const [language, setLanguage] = useState("es");
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [requestCount, setRequestCount] = useState(0);
    const INITIAL_LIMIT = 10; // Límite inicial de noticias
    const [showError, setShowError] = useState(false); // Estado para mostrar el mensaje de error

    const getData = async (limit = INITIAL_LIMIT) => {
        if (requestCount >= MAX_REQUESTS) {
            setError(translations[language].requestLimitReached);
            setShowError(true); // Mantiene el error visible
            return;
        }

        setLoading(true);
        setError(null);
        setShowError(false); // Oculta el mensaje de error al iniciar una nueva solicitud

        try {
            const data = await fetchNews(search, page, limit); // Llama a la API
            if (data.length === 0 && search) {
                setError(translations[language].noResultsMessage);
                setShowError(true); // Mantiene el error visible
            } else {
                setNewsData((prevData) => [...prevData, ...data]); // Mantiene las noticias existentes
                setRequestCount(prevCount => prevCount + 1); // Incrementa el contador solo si hay datos
                setShowError(false); // Oculta el mensaje de error si la consulta es exitosa
            }
        } catch (error) {
            console.error("Error al obtener noticias:", error); // Depuración
            setError(translations[language].noResultsMessage); // Mensaje genérico
            setShowError(true); // Mantiene el error visible
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        getData(); // Llama a getData en cada renderizado al cambiar page o search
    }, [page, search]);

    useEffect(() => {
        const handleScroll = () => {
            if (window.innerHeight + document.documentElement.scrollTop >= document.documentElement.offsetHeight - 1 && !loading) {
                if (requestCount < MAX_REQUESTS) {
                    setPage((prevPage) => prevPage + 1); // Aumenta la página para cargar más noticias
                }
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, [loading, requestCount]);

    const handleInput = (e) => {
        setSearch(e.target.value);
    };

    const handleSearch = () => {
        setPage(1); // Resetea la página al buscar
        setNewsData([]); // Resetea las noticias al buscar
        setRequestCount(0); // Resetea el contador de solicitudes al buscar
        getData(); // Llama a getData cuando se busca
    };

    const handleKeyDown = (e) => {
        if (e.key === 'Enter') {
            handleSearch(); // Llama a la función de búsqueda al presionar Enter
        }
    };

    const toggleLanguage = () => {
        setLanguage((prevLanguage) => (prevLanguage === "es" ? "en" : "es"));
        setNewsData([]); // Resetea los datos al cambiar de idioma
        setPage(1); // Resetea la paginación
        setError(null); // Resetea posibles errores previos
        setRequestCount(0); // Resetea el contador de solicitudes al cambiar de idioma
    };

    return (
        <div className="App">
            <header className="header">
                <h1>{translations[language].title}</h1>
            </header>
            <div className="mainContent">
                <main className="centralContent">
                    <p className='head'>{translations[language].header}</p>
                    <div>
                        {loading ? (
                            <Loader />
                        ) : showError && error ? ( // Muestra el error si está activo
                            <ErrorDisplay error={error} />
                        ) : newsData.length > 0 ? (
                            <Card data={newsData} />
                        ) : (
                            <p>{translations[language].noInputMessage}</p>
                        )}
                    </div>
                </main>
                <aside className="sidebar rightSidebar">
                    <div className='searchBar'>
                        <input
                            type='text'
                            placeholder={translations[language].placeholder}
                            value={search}
                            onChange={handleInput}
                            onKeyDown={handleKeyDown}
                        />
                        <button onClick={handleSearch}>{translations[language].searchButton}</button>
                    </div>
                    <div className="buttonContainer">
                        <LanguageToggle toggleLanguage={toggleLanguage} currentLanguage={language} />
                    </div>
                </aside>
            </div>
            <footer className="footer">
                <p>Isaac Barco</p>
            </footer>
        </div>
    );
};

export default NewsApp;
