import React, { useEffect, useState } from 'react';
import { fetchNews } from '../utils/api'; 
import { translations, MAX_REQUESTS } from '../utils/constants'; 
import '../styles/index.css'; 
import '../styles/App.css'; 
import LanguageToggle from './LanguageToggle'; 
import Card from './Card';
import ErrorMessage from './ErrorMessage';
import Loading from './Loading';
import SearchBar from './SearchBar'; 

const NewsApp = () => {
    const [search, setSearch] = useState("");
    const [newsData, setNewsData] = useState([]);
    const [page, setPage] = useState(1);
    const [language, setLanguage] = useState("es");
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [requestCount, setRequestCount] = useState(0);

    const getData = async () => {
        if (requestCount >= MAX_REQUESTS) {
            setError(translations[language].requestLimitReached);
            return;
        }

        setLoading(true);
        setError(null);

        try {
            const data = await fetchNews(search, page);

            if (!data || !Array.isArray(data)) {
                throw new Error(translations[language].invalidResponse); // Maneja respuesta inválida
            }

            console.log(data); // Verificar lo que devuelve la API
            setNewsData((prevData) => [...prevData, ...data]);
            setRequestCount(prevCount => prevCount + 1);
        } catch (error) {
            setError(error.message || translations[language].genericError);
        } finally {
            setLoading(false);
        }
    };

    // Reinicia la búsqueda cuando cambie el término de búsqueda
    useEffect(() => {
        if (search) {
            setNewsData([]); // Reinicia datos al cambiar la búsqueda
            setPage(1); // Reinicia a la primera página
            setRequestCount(0); // Reinicia el contador de peticiones
        }
    }, [search]);

    // Hace la petición cuando cambia la página o la búsqueda
    useEffect(() => {
        if (page === 1 && !search) return; // Evita una llamada inicial no deseada
        getData();
    }, [page, search]);

    // Detecta scroll infinito
    useEffect(() => {
        const handleScroll = () => {
            if (window.innerHeight + document.documentElement.scrollTop >= document.documentElement.offsetHeight - 1 && !loading) {
                console.log("Cargando más noticias...");
                setPage((prevPage) => prevPage + 1);
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, [loading]);

    // Manejo del input de búsqueda
    const handleInput = (e) => {
        setSearch(e.target.value);
    };

    const handleSearch = () => {
        setPage(1);
        setNewsData([]);
        getData();
    };

    const handleKeyDown = (e) => {
        if (e.key === 'Enter') {
            handleSearch(); 
        }
    };

    // Cambia el idioma y reinicia la búsqueda
    const toggleLanguage = () => {
        setLanguage((prevLanguage) => (prevLanguage === "es" ? "en" : "es"));
        setNewsData([]);
        setPage(1);
        setError(null);
    };

    return (
        <div className="App">
            <header className="header">
                <h1>{translations[language].title}</h1>
            </header>
            <div className="mainContent">
                <main className="centralContent">
                    <p className='head'>{translations[language].header}</p>
                    <div>
                        {loading ? (
                            <Loading /> 
                        ) : error ? (
                            <ErrorMessage message={error} /> 
                        ) : newsData.length > 0 ? (
                            <Card data={newsData} />
                        ) : (
                            <p>{translations[language].noNews}</p>
                        )}
                    </div>
                </main>
                <aside className="sidebar rightSidebar">
                    <SearchBar 
                        value={search}
                        onChange={handleInput}
                        onKeyDown={handleKeyDown}
                        onSearch={handleSearch}
                        placeholder={translations[language].placeholder}
                        searchButtonText={translations[language].searchButton}
                    />
                    <div className="buttonContainer">
                        <LanguageToggle toggleLanguage={toggleLanguage} currentLanguage={language} />
                    </div>
                </aside>
            </div>
            <footer className="footer">
                <p>Isaac Barco</p>
            </footer>
        </div>
    );
};

export default NewsApp;
